import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchGalleryById, uploadPhotos } from '../services/api';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';

const GalleryManager = () => {
  const { id } = useParams();
  const [gallery, setGallery] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previewUrls, setPreviewUrls] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const owner = 'admin'; // Puedes cambiar esto por el usuario real si tienes auth

  useEffect(() => {
    async function loadGallery() {
      try {
        setLoading(true);
        const data = await fetchGalleryById(id);
        setGallery(data);
        setPhotos(data.photos || []);
      } catch (e) {
        setError('No se pudo cargar la galería');
      } finally {
        setLoading(false);
      }
    }
    loadGallery();
  }, [id]);

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(files);
    setPreviewUrls(files.map(file => URL.createObjectURL(file)));
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFiles.length) return;
    setLoading(true);
    setError(null);
    try {
      await uploadPhotos({ galleryId: id, owner, files: selectedFiles });
      // Recargar galería
      const data = await fetchGalleryById(id);
      setGallery(data);
      setPhotos(data.photos || []);
      setSelectedFiles([]);
      setPreviewUrls([]);
    } catch (e) {
      setError('Error al subir fotos');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 32 }}>
      <h1>Gestión de Galería</h1>
      {loading && <p>Cargando...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {gallery && (
        <>
          <h2>{gallery.title}</h2>

          {/* Permisos de galería */}
          <div style={{ marginBottom: 24 }}>
            <FormControlLabel
              control={
                <Switch
                  checked={gallery.allowDownload}
                  onChange={handleAllowDownloadChange}
                />
              }
              label="Permitir descarga"
            />
            <FormControlLabel
              control={
                <Switch
                  checked={gallery.allowFinalDownload}
                  onChange={handleAllowFinalDownloadChange}
                />
              }
              label="Permitir descarga final"
            />
          </div>

          <form onSubmit={handleUpload} style={{ marginBottom: 24 }}>
            <label>
              Subir fotos (múltiple):
              <input type="file" multiple accept="image/*" onChange={handleFileChange} />
            </label>
            <button type="submit" disabled={loading || !selectedFiles.length} style={{ marginLeft: 8 }}>
              Subir
            </button>
          </form>
          {previewUrls.length > 0 && (
            <div style={{ display: 'flex', gap: 8, marginBottom: 24 }}>
              {previewUrls.map((url, idx) => (
                <img key={idx} src={url} alt="preview" style={{ width: 80, height: 80, objectFit: 'cover', borderRadius: 4, border: '1px solid #ccc' }} />
              ))}
            </div>
          )}
          <h3>Fotos en la galería</h3>
          {photos.length === 0 ? (
            <p>No hay fotos aún.</p>
          ) : (
            <Grid container spacing={2}>
              {photos.map(photo => (
                <Grid item xs={12} sm={6} md={4} lg={3} key={photo._id}>
                  <Card>
                    <CardMedia
                      component="img"
                      height="180"
                      image={photo.watermarkedUrl || photo.url}
                      alt={photo.name || 'foto'}
                      style={{ objectFit: 'cover' }}
                    />
                    <CardContent>
                      <Typography variant="subtitle1" noWrap>{photo.name || 'Sin nombre'}</Typography>
                      {photo.createdAt && (
                        <Typography variant="caption" color="textSecondary">
                          {new Date(photo.createdAt).toLocaleString()}
                        </Typography>
                      )}
                    </CardContent>
                    <CardActions>
                      <IconButton
                        aria-label="ver"
                        onClick={() => window.open(photo.watermarkedUrl || photo.url, '_blank')}
                      >
                        <span className="material-icons">zoom_in</span>
                      </IconButton>
                      <IconButton
                        aria-label="eliminar"
                        onClick={async () => {
                          if (window.confirm('¿Eliminar esta foto?')) {
                            try {
                              await import('../services/api').then(mod => mod.deletePhoto(photo._id));
                              setPhotos(photos.filter(p => p._id !== photo._id));
                            } catch {
                              alert('Error al eliminar foto');
                            }
                          }
                        }}
                        style={{ color: '#f44336' }}
                      >
                        <span className="material-icons">delete</span>
                      </IconButton>
                    </CardActions>
                  </Card>
                </Grid>
              ))}
            </Grid>
          )}
        </>
      )}
    </div>
  );
};

export default GalleryManager;
