import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchGalleryById, uploadPhotos } from '../services/api';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';
import Button from '@mui/material/Button';

const GalleryManager = () => {
  const { id } = useParams();
  const [gallery, setGallery] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previewUrls, setPreviewUrls] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState('');
  const owner = 'admin'; // Puedes cambiar esto por el usuario real si tienes auth

  useEffect(() => {
    async function loadGallery() {
      try {
        setLoading(true);
        const data = await fetchGalleryById(id);
        setGallery(data);
        setPhotos(data.photos || []);
      } catch (e) {
        setError('No se pudo cargar la galería');
      } finally {
        setLoading(false);
      }
    }
    loadGallery();
  }, [id]);

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(files);
    setPreviewUrls(files.map(file => URL.createObjectURL(file)));
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFiles.length) return;
    setLoading(true);
    setError(null);
    try {
      await uploadPhotos({ galleryId: id, owner, files: selectedFiles });
      // Recargar galería
      const data = await fetchGalleryById(id);
      setGallery(data);
      setPhotos(data.photos || []);
      setSelectedFiles([]);
      setPreviewUrls([]);
    } catch (e) {
      setError('Error al subir fotos');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Gestión de Galería
        </Typography>
        {loading && <Typography color="text.secondary">Cargando...</Typography>}
        {error && <MuiAlert severity="error" sx={{ mb: 2 }}>{error}</MuiAlert>}
        {gallery && (
          <>
            <Typography variant="h5" sx={{ mb: 2 }}>{gallery.title}</Typography>

            {/* Permisos de galería */}
            <Paper variant="outlined" sx={{ p: 2, mb: 3, display: 'flex', gap: 2 }}>
              <FormControlLabel
                control={
                  <Switch
                    checked={gallery.allowDownload}
                    onChange={handleAllowDownloadChange}
                  />
                }
                label="Permitir descarga"
              />
              <FormControlLabel
                control={
                  <Switch
                    checked={gallery.allowFinalDownload}
                    onChange={handleAllowFinalDownloadChange}
                  />
                }
                label="Permitir descarga final"
              />
            </Paper>

            <Paper variant="outlined" sx={{ p: 2, mb: 3 }}>
              <form onSubmit={handleUpload} style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
                <label style={{ flex: 1 }}>
                  <Typography variant="body1">Subir fotos (múltiple):</Typography>
                  <input type="file" multiple accept="image/*" onChange={handleFileChange} style={{ marginTop: 8 }} />
                </label>
                <Button type="submit" variant="contained" color="primary" disabled={loading || !selectedFiles.length}>
                  Subir
                </Button>
              </form>
              {previewUrls.length > 0 && (
                <div style={{ display: 'flex', gap: 8, marginTop: 16 }}>
                  {previewUrls.map((url, idx) => (
                    <img key={idx} src={url} alt="preview" style={{ width: 80, height: 80, objectFit: 'cover', borderRadius: 4, border: '1px solid #ccc' }} />
                  ))}
                </div>
              )}
            </Paper>

            <Typography variant="h6" sx={{ mb: 2 }}>Fotos en la galería</Typography>
            {photos.length === 0 ? (
              <Typography color="text.secondary">No hay fotos aún.</Typography>
            ) : (
              <Grid container spacing={2}>
                {photos.map(photo => (
                  <Grid item xs={12} sm={6} md={4} lg={3} key={photo._id}>
                    <Card>
                      <CardMedia
                        component="img"
                        height="180"
                        image={photo.watermarkedUrl || photo.url}
                        alt={photo.name || 'foto'}
                        style={{ objectFit: 'cover' }}
                      />
                      <CardContent>
                        <Typography variant="subtitle1" noWrap>{photo.name || 'Sin nombre'}</Typography>
                        {photo.createdAt && (
                          <Typography variant="caption" color="textSecondary">
                            {new Date(photo.createdAt).toLocaleString()}
                          </Typography>
                        )}
                      </CardContent>
                      <CardActions>
                        <IconButton
                          aria-label="ver"
                          onClick={() => window.open(photo.watermarkedUrl || photo.url, '_blank')}
                        >
                          <span className="material-icons">zoom_in</span>
                        </IconButton>
                        <IconButton
                          aria-label="eliminar"
                          onClick={async () => {
                            if (window.confirm('¿Eliminar esta foto?')) {
                              try {
                                await import('../services/api').then(mod => mod.deletePhoto(photo._id));
                                setPhotos(photos.filter(p => p._id !== photo._id));
                              } catch {
                                setError('Error al eliminar foto');
                              }
                            }
                          }}
                          style={{ color: '#f44336' }}
                        >
                          <span className="material-icons">delete</span>
                        </IconButton>
                      </CardActions>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            )}
          </>
        )}
      </Paper>
      {/* Snackbar para feedback */}
      <Snackbar
        open={!!message}
        autoHideDuration={3000}
        onClose={() => setMessage('')}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <MuiAlert onClose={() => setMessage('')} severity="success" sx={{ width: '100%' }}>
          {message}
        </MuiAlert>
      </Snackbar>
      {error && (
        <Snackbar
          open={!!error}
          autoHideDuration={4000}
          onClose={() => setError('')}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <MuiAlert onClose={() => setError('')} severity="error" sx={{ width: '100%' }}>
            {error}
          </MuiAlert>
        </Snackbar>
      )}
    </Container>
  );
};

export default GalleryManager;
