// Servicio API para consumir el backend
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4000';

export async function fetchGalleries() {
  const res = await fetch(`${API_URL}/galleries`);
  if (!res.ok) throw new Error('Error al obtener galerías');
  return res.json();
}

export async function createGallery(data) {
  const res = await fetch(`${API_URL}/galleries`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(data),
  });
  if (!res.ok) throw new Error('Error al crear galería');
  return res.json();
}

export async function fetchGalleryById(id) {
  console.log(`Obteniendo galería con ID: ${id}`);
  try {
    const res = await fetch(`${API_URL}/galleries/${id}`, {
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    });

    if (!res.ok) {
      const errorData = await res.json().catch(() => ({}));
      console.error('Error al obtener galería:', { 
        status: res.status, 
        statusText: res.statusText,
        error: errorData 
      });
      throw new Error(errorData.error || `Error al obtener galería: ${res.status} ${res.statusText}`);
    }

    const data = await res.json();

    // Asegurarse de que la respuesta tiene la estructura esperada
    if (!data || !data.id) {
      console.error('Datos de galería inválidos:', data);
      throw new Error('La respuesta del servidor no contiene datos válidos de la galería');
    }

    console.log('Datos de la galería recibidos:', data);

    // Mapear los nombres de campos si es necesario
    const mappedData = {
      ...data,
      // Asegurarse de que los campos booleanos sean booleanos
      is_active: Boolean(data.is_active),
      allow_download: Boolean(data.allow_download),
      allow_final_download: Boolean(data.allow_final_download),
      // Asegurarse de que las fotos sean un array
      photos: Array.isArray(data.photos) ? data.photos : []
    };

    return mappedData;
  } catch (error) {
    console.error('Error en fetchGalleryById:', error);
    throw error; // Relanzar el error para que lo maneje el componente
  }
}

export async function setAllowDownload(id, allowDownload) {
  try {
    console.log('Enviando PATCH a /allow-download con:', { id, allowDownload });
    const res = await fetch(`${API_URL}/galleries/${id}/allow-download`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
