export async function uploadPhotos({ galleryId, owner, files }) {
  const formData = new FormData();
  formData.append('galleryId', galleryId);
  formData.append('owner', owner);
  for (const file of files) {
    formData.append('photos', file);
  }
  const res = await fetch(`${API_URL}/photos/upload-multiple`, {
    method: 'POST',
    body: formData,
  });
  if (!res.ok) throw new Error('Error al subir fotos');
  return res.json();
}

export async function deleteAllPhotosFromGallery(galleryId) {
  const res = await fetch(`${API_URL}/photos/gallery/${galleryId}`, { method: 'DELETE' });
  if (!res.ok) throw new Error('Error al eliminar todas las fotos');
  return await res.json();
}

export async function deleteGallery(id) {
  try {
    console.log(`Eliminando galería con ID: ${id}`);
    const res = await fetch(`${API_URL}/galleries/${id}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json'
      }
    });

    const contentType = res.headers.get('content-type');
    let errorData = {};

    if (!res.ok) {
      try {
        errorData = await res.json();
      } catch (jsonError) {
        const text = await res.text();
        console.error('Error al parsear respuesta de error:', text);
        throw new Error(`Error al eliminar la galería: ${res.status} ${res.statusText}`);
      }
      throw new Error(errorData.message || `Error al eliminar la galería: ${res.status} ${res.statusText}`);
    }

    try {
      return await res.json();
    } catch (jsonError) {
      console.log('La respuesta no contiene JSON, se asume éxito');
      return { success: true };
    }
  } catch (error) {
    console.error('Error en deleteGallery:', error);
    throw error;
  }
}

export async function updateGalleryStatus(id, isActive) {
  console.log(`Actualizando estado de la galería ${id} a ${isActive ? 'activo' : 'inactivo'}`);
  try {
    const res = await fetch(`${API_URL}/galleries/${id}/status`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ isActive }),
    });

    const responseData = await res.json().catch(() => ({}));

    if (!res.ok) {
      console.error('Error en la respuesta del servidor:', {
        status: res.status,
        statusText: res.statusText,
        response: responseData
      });
      throw new Error(responseData.message || 'Error al actualizar el estado de la galería');
    }

    console.log('Estado de la galería actualizado correctamente:', responseData);
    return responseData;
  } catch (error) {
    console.error('Error en updateGalleryStatus:', error);
    throw error;
  }
}

export async function deletePhoto(photoId) {
  const res = await fetch(`${API_URL}/photos/${photoId}`, {
    method: 'DELETE',
  });
  if (!res.ok) throw new Error('Error al eliminar foto');
  return res.json();
}
