import React, { useEffect, useState, useMemo } from 'react';
import { Link } from 'react-router-dom';
import { 
  fetchGalleries, 
  createGallery, 
  deleteGallery,
  updateGalleryStatus
} from '../services/api';
import {
  Box, 
  Button, 
  Card, 
  CardContent, 
  CardMedia, 
  CircularProgress, 
  Container, 
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Grid, 
  IconButton, 
  Paper, 
  Snackbar,
  TextField, 
  Typography, 
  Chip,
  Tooltip
} from '@mui/material';
import {
  Add as AddIcon,
  PhotoLibrary as PhotoLibraryIcon,
  Collections as CollectionsIcon,
  Delete as DeleteIcon,
  Edit as EditIcon,
  Visibility as VisibilityIcon,
  Search as SearchIcon,
  FilterList as FilterListIcon,
  Close as CloseIcon,
  Link as LinkIcon
} from '@mui/icons-material';

const Dashboard = () => {
  const [galleries, setGalleries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('all'); // 'all', 'active', 'inactive'
  const [creating, setCreating] = useState(false);
  const [formError, setFormError] = useState(null);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: ''
  });
  const [newGallery, setNewGallery] = useState({
    title: '',
    selectionLimit: 5
  });

  // Mostrar notificación
  const showSnackbar = (message) => {
    setSnackbar({ open: true, message });
  };

  // Cerrar notificación
  const handleCloseSnackbar = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setSnackbar({ ...snackbar, open: false });
  };

  // Manejar cambio de estado de galería
  const handleStatusChange = async (galleryId, isActive) => {
    try {
      await updateGalleryStatus(galleryId, !isActive);
      await loadGalleries();
      showSnackbar(`Galería ${isActive ? 'desactivada' : 'activada'} correctamente`);
    } catch (err) {
      console.error('Error al actualizar estado:', err);
      showSnackbar('Error al actualizar el estado de la galería');
    }
  };

  // Manejar eliminación de galería
  const handleDeleteGallery = async (galleryId) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar esta galería? Esta acción no se puede deshacer.')) {
      try {
        await deleteGallery(galleryId);
        await loadGalleries();
        showSnackbar('Galería eliminada correctamente');
      } catch (err) {
        console.error('Error al eliminar galería:', err);
        showSnackbar('Error al eliminar la galería');
      }
    }
  };

  // Manejar creación de nueva galería
  const handleCreateGallery = async (e) => {
    e.preventDefault();
    setFormError(null);

    if (!newGallery.title.trim()) {
      setFormError('El título es requerido');
      return;
    }

    try {
      setCreating(true);
      await createGallery({
        title: newGallery.title,
        selectionLimit: parseInt(newGallery.selectionLimit, 10)
      });
      await loadGalleries();
      setNewGallery({ title: '', selectionLimit: 5 });
      setCreating(false);
      showSnackbar('Galería creada exitosamente');
    } catch (err) {
      console.error('Error al crear galería:', err);
      setFormError(err.message || 'Error al crear la galería');
      setCreating(false);
    }
  };

  // Cargar galerías
  const loadGalleries = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await fetchGalleries();
      setGalleries(data);
    } catch (err) {
      setError(err.message);
      showSnackbar('Error al cargar las galerías: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadGalleries();
  }, []);

  // Filtrar galerías
  const filteredGalleries = useMemo(() => {
    return galleries.filter(gallery => {
      const matchesSearch = gallery.title.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesFilter = filter === 'all' || 
                         (filter === 'active' && gallery.is_active) ||
                         (filter === 'inactive' && !gallery.is_active);
      return matchesSearch && matchesFilter;
    });
  }, [galleries, searchTerm, filter]);

  // Estadísticas
  const stats = useMemo(() => ({
    totalGalleries: galleries.length,
    activeGalleries: galleries.filter(g => g.is_active).length,
    totalPhotos: galleries.reduce((sum, g) => sum + (g.photos_count || 0), 0),
    recentGalleries: galleries
      .sort((a, b) => new Date(b.created_at) - new Date(a.created_at))
      .slice(0, 3)
  }), [galleries]);



  if (loading && galleries.length === 0) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      {/* Encabezado */}
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={4}>
        <Typography variant="h4" component="h1" gutterBottom>
          Panel de Control
        </Typography>
        <Button 
          variant="contained" 
          color="primary" 
          startIcon={<AddIcon />}
          onClick={() => setCreating(true)}
        >
          Nueva Galería
        </Button>
      </Box>

      {/* Diálogo de Nueva Galería */}
      <Dialog open={creating} onClose={() => !creating && setCreating(false)} maxWidth="sm" fullWidth>
        <form onSubmit={handleCreateGallery}>
          <DialogTitle>Nueva Galería</DialogTitle>
          <DialogContent>
            <TextField
              autoFocus
              margin="dense"
              label="Título de la galería"
              type="text"
              fullWidth
              variant="outlined"
              value={newGallery.title}
              onChange={(e) => setNewGallery({...newGallery, title: e.target.value})}
              required
              sx={{ mb: 2 }}
            />
            <TextField
              margin="dense"
              label="Límite de selección"
              type="number"
              fullWidth
              variant="outlined"
              value={newGallery.selectionLimit}
              onChange={(e) => setNewGallery({...newGallery, selectionLimit: e.target.value})}
              inputProps={{ min: 1, max: 100 }}
              helperText="Número máximo de fotos que los clientes pueden seleccionar"
            />
            {formError && (
              <Typography color="error" sx={{ mt: 1 }}>
                {formError}
              </Typography>
            )}
          </DialogContent>
          <DialogActions sx={{ p: 2 }}>
            <Button 
              onClick={() => setCreating(false)}
              disabled={creating}
            >
              Cancelar
            </Button>
            <Button 
              type="submit" 
              variant="contained" 
              color="primary"
              disabled={!newGallery.title.trim() || creating}
            >
              {creating ? 'Creando...' : 'Crear Galería'}
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      {/* Notificación */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        message={snackbar.message}
        action={
          <IconButton
            size="small"
            color="inherit"
            onClick={handleCloseSnackbar}
          >
            <CloseIcon fontSize="small" />
          </IconButton>
        }
      />

      {/* Estadísticas */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Paper sx={{ p: 2, textAlign: 'center' }}>
            <CollectionsIcon color="primary" fontSize="large" />
            <Typography variant="h6">{stats.totalGalleries}</Typography>
            <Typography variant="body2" color="textSecondary">Galerías Totales</Typography>
          </Paper>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Paper sx={{ p: 2, textAlign: 'center' }}>
            <PhotoLibraryIcon color="success" fontSize="large" />
            <Typography variant="h6">{stats.activeGalleries}</Typography>
            <Typography variant="body2" color="textSecondary">Galerías Activas</Typography>
          </Paper>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Paper sx={{ p: 2, textAlign: 'center' }}>
            <PhotoLibraryIcon color="info" fontSize="large" />
            <Typography variant="h6">{stats.totalPhotos}</Typography>
            <Typography variant="body2" color="textSecondary">Fotos Totales</Typography>
          </Paper>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Paper sx={{ p: 2, textAlign: 'center' }}>
            <Typography variant="h6" gutterBottom>Acciones Rápidas</Typography>
            <Button 
              variant="outlined" 
              size="small" 
              component={Link} 
              to="/watermark"
              startIcon={<EditIcon />}
              fullWidth
              sx={{ mb: 1 }}
            >
              Marca de Agua
            </Button>
            <Button 
              variant="outlined" 
              size="small" 
              component={Link} 
              to="/gallery/new"
              startIcon={<AddIcon />}
              fullWidth
            >
              Nueva Galería
            </Button>
          </Paper>
        </Grid>
      </Grid>

      {/* Filtros y Búsqueda */}
      <Paper sx={{ p: 2, mb: 3 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Buscar galerías..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              InputProps={{
                startAdornment: <SearchIcon color="action" sx={{ mr: 1 }} />
              }}
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <Box display="flex" gap={1}>
              <Chip
                label="Todas"
                onClick={() => setFilter('all')}
                color={filter === 'all' ? 'primary' : 'default'}
                variant={filter === 'all' ? 'filled' : 'outlined'}
              />
              <Chip
                label="Activas"
                onClick={() => setFilter('active')}
                color={filter === 'active' ? 'primary' : 'default'}
                variant={filter === 'active' ? 'filled' : 'outlined'}
              />
              <Chip
                label="Inactivas"
                onClick={() => setFilter('inactive')}
                color={filter === 'inactive' ? 'primary' : 'default'}
                variant={filter === 'inactive' ? 'filled' : 'outlined'}
              />
              <Box flexGrow={1} />
              <Tooltip title="Filtros">
                <IconButton>
                  <FilterListIcon />
                </IconButton>
              </Tooltip>
            </Box>
          </Grid>
        </Grid>
      </Paper>

      {/* Lista de Galerías */}
      <Grid container spacing={3}>
        {loading && filteredGalleries.length === 0 ? (
          <Grid item xs={12} sx={{ textAlign: 'center', py: 4 }}>
            <CircularProgress />
            <Typography variant="body1" sx={{ mt: 2 }}>Cargando galerías...</Typography>
          </Grid>
        ) : error ? (
          <Grid item xs={12}>
            <Paper sx={{ p: 3, textAlign: 'center', bgcolor: 'error.light' }}>
              <Typography color="error">{error}</Typography>
            </Paper>
          </Grid>
        ) : filteredGalleries.length === 0 ? (
          <Grid item xs={12}>
            <Paper sx={{ p: 4, textAlign: 'center' }}>
              <PhotoLibraryIcon sx={{ fontSize: 48, color: 'text.secondary', mb: 2 }} />
              <Typography variant="h6" color="textSecondary" gutterBottom>
                {searchTerm || filter !== 'all' 
                  ? 'No se encontraron galerías que coincidan con los filtros' 
                  : 'Aún no tienes galerías creadas'}
              </Typography>
              <Button
                variant="contained"
                color="primary"
                startIcon={<AddIcon />}
                onClick={() => setCreating(true)}
                sx={{ mt: 2 }}
              >
                Crear tu primera galería
              </Button>
            </Paper>
          </Grid>
        ) : (
          filteredGalleries.map((gallery) => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={gallery.id}>
              <Card sx={{ 
                height: '100%', 
                display: 'flex', 
                flexDirection: 'column',
                transition: 'transform 0.2s, box-shadow 0.2s',
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: 3
                }
              }}>
                <CardMedia
                  component="div"
                  sx={{
                    pt: '56.25%', // 16:9
                    position: 'relative',
                    bgcolor: 'grey.200',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    color: 'text.secondary',
                    backgroundImage: gallery.cover_photo_url ? `url(${gallery.cover_photo_url})` : 'none',
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                  }}
                >
                  {!gallery.cover_photo_url && (
                    <PhotoLibraryIcon fontSize="large" />
                  )}
                  <Box 
                    sx={{
                      position: 'absolute',
                      top: 8,
                      right: 8,
                      display: 'flex',
                      gap: 1
                    }}
                  >
                    <Chip 
                      label={gallery.is_active ? 'Activa' : 'Inactiva'} 
                      color={gallery.is_active ? 'success' : 'default'}
                      size="small"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleStatusChange(gallery.id, gallery.is_active);
                      }}
                      clickable
                    />
                  </Box>
                </CardMedia>
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h6" component="h2" noWrap>
                    {gallery.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                    {new Date(gallery.created_at).toLocaleDateString()}
                  </Typography>
                  <Box sx={{ display: 'flex', gap: 1, mb: 1, flexWrap: 'wrap' }}>
                    <Chip 
                      icon={<PhotoLibraryIcon fontSize="small" />} 
                      label={`${gallery.photos_count || 0} fotos`} 
                      size="small" 
                      variant="outlined"
                    />
                    {gallery.allow_download && (
                      <Chip 
                        label="Descarga" 
                        size="small" 
                        color="primary"
                        variant="outlined"
                      />
                    )}
                    {gallery.allow_final_download && (
                      <Chip 
                        label="Descarga HD" 
                        size="small" 
                        color="secondary"
                        variant="outlined"
                      />
                    )}
                  </Box>
                </CardContent>
                <Box sx={{ p: 2, pt: 0, display: 'flex', justifyContent: 'space-between' }}>
                  <Button 
                    size="small" 
                    component={Link} 
                    to={`/gallery/${gallery.id}`}
                    startIcon={<EditIcon />}
                  >
                    Editar
                  </Button>
                  <Box>
                    <Tooltip title="Vista previa">
                      <IconButton 
                        size="small" 
                        component={Link} 
                        to={`/client/${gallery.link}`}
                        target="_blank"
                      >
                        <VisibilityIcon fontSize="small" />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Copiar enlace">
                      <IconButton 
                        size="small"
                        onClick={(e) => {
                          e.stopPropagation();
                          navigator.clipboard.writeText(`${window.location.origin}/client/${gallery.link}`);
                          showSnackbar('Enlace copiado al portapapeles');
                        }}
                      >
                        <LinkIcon fontSize="small" />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Eliminar">
                      <IconButton 
                        size="small" 
                        color="error"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDeleteGallery(gallery.id);
                        }}
                      >
                        <DeleteIcon fontSize="small" />
                      </IconButton>
                    </Tooltip>
                  </Box>
                </Box>
              </Card>
            </Grid>
          ))
        )}
      </Grid>
    </Container>
  );
};

export default Dashboard;
