import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  Container, 
  Grid, 
  Card, 
  CardMedia, 
  CardActions, 
  Button, 
  Typography, 
  CircularProgress, 
  Snackbar, 
  Alert, 
  Box,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions as MuiDialogActions,
  styled
} from '@mui/material';
import { CheckCircle, Download, Close, Check, ZoomIn } from '@mui/icons-material';

const ClientGallery = () => {
  const [gallery, setGallery] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [selected, setSelected] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [downloadDialogOpen, setDownloadDialogOpen] = useState(false);
  const [previewOpen, setPreviewOpen] = useState(false);
  const [currentPreview, setCurrentPreview] = useState(null);
  const navigate = useNavigate();
  const { link } = useParams();
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4000';

  // Cargar la galería y sus fotos
  useEffect(() => {
    const fetchGallery = async () => {
      setLoading(true);
      try {
        // Obtener información de la galería
        const galleryRes = await fetch(`${API_URL}/galleries/${link}`);
        if (!galleryRes.ok) throw new Error('No se pudo cargar la galería');
        const galleryData = await galleryRes.json();

        // Siempre cargamos la galería, independientemente de los permisos
        setGallery(galleryData);
        setPhotos(galleryData.photos || []);

        // Cargar las fotos ya seleccionadas si existen
        if (galleryData.selected_photos && galleryData.selected_photos.length > 0) {
          setSelected(galleryData.selected_photos.map(p => p.photo_id));
        }

      } catch (err) {
        setError(err.message || 'Error al cargar la galería');
      } finally {
        setLoading(false);
      }
    };

    fetchGallery();
  }, [link, API_URL]);

  // Alternar selección de foto
  const toggleSelect = (photoId) => {
    setSelected(prev => {
      // Si la foto ya está seleccionada, la quitamos
      if (prev.includes(photoId)) {
        return prev.filter(id => id !== photoId);
      }

      // Verificar si se puede seleccionar más fotos
      if (gallery && prev.length >= gallery.selection_limit) {
        setMessage(`Solo puedes seleccionar hasta ${gallery.selection_limit} fotos`);
        return prev;
      }

      // Añadir la nueva foto seleccionada
      return [...prev, photoId];
    });
  };

  // Abrir vista previa de la foto
  const handlePreview = (photo) => {
    setCurrentPreview(photo);
    setPreviewOpen(true);
  };

  // Descargar foto individual
  const handleDownload = (photo) => {
    if (!gallery?.allow_download) {
      setMessage('La descarga de fotos no está permitida en esta galería');
      return;
    }

    let downloadUrl = '';

    // 1. Si tiene permiso para descargar sin marca de agua y la imagen tiene URL sin marca
    if (gallery.allow_final_download && photo.url) {
      downloadUrl = photo.url.startsWith('http') 
        ? photo.url 
        : `${API_URL}/${photo.url.replace(/\\\\/g, '/')}`;
    } 
    // 2. Si no tiene permiso, usar la versión con marca de agua si existe
    else if (photo.watermarked_url) {
      downloadUrl = photo.watermarked_url.startsWith('http')
        ? photo.watermarked_url
        : `${API_URL}/${photo.watermarked_url.replace(/\\\\/g, '/')}`;
    }
    // 3. Si no hay versión con marca de agua, usar la original
    else if (photo.url) {
      downloadUrl = photo.url.startsWith('http')
        ? photo.url
        : `${API_URL}/${photo.url.replace(/\\\\/g, '/')}`;
    }

    // Abrir la URL si se encontró una válida
    if (downloadUrl) {
      window.open(downloadUrl, '_blank');
    } else {
      setError('No se pudo encontrar la imagen para descargar');
    }
  };

  // Guardar selección
  const handleSaveSelection = async () => {
    if (!gallery) return;

    try {
      const response = await fetch(`${API_URL}/galleries/${gallery.id}/select-photos`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ photoIds: selected })
      });

      if (!response.ok) throw new Error('Error al guardar la selección');

      setMessage('Selección guardada correctamente');
      setDownloadDialogOpen(true);
    } catch (err) {
      setError(err.message || 'Error al guardar la selección');
    }
  };

  // Descargar fotos seleccionadas
  const handleDownloadSelected = async () => {
    if (selected.length === 0) {
      setMessage('Selecciona al menos una foto para descargar');
      return;
    }

    if (!gallery?.allow_download) {
      setMessage('La descarga de fotos no está permitida en esta galería');
      return;
    }

    try {
      // Abrir cada descarga en una nueva pestaña
      selected.forEach(photoId => {
        const photo = photos.find(p => p.id === photoId);
        if (photo) {
          let downloadUrl = '';

          // 1. Si tiene permiso para descargar sin marca de agua y la imagen tiene URL sin marca
          if (gallery.allow_final_download && photo.url) {
            downloadUrl = photo.url.startsWith('http') 
              ? photo.url 
              : `${API_URL}/${photo.url.replace(/\\\\/g, '/')}`;
          } 
          // 2. Si no tiene permiso, usar la versión con marca de agua si existe
          else if (photo.watermarked_url) {
            downloadUrl = photo.watermarked_url.startsWith('http')
              ? photo.watermarked_url
              : `${API_URL}/${photo.watermarked_url.replace(/\\\\/g, '/')}`;
          }
          // 3. Si no hay versión con marca de agua, usar la original
          else if (photo.url) {
            downloadUrl = photo.url.startsWith('http')
              ? photo.url
              : `${API_URL}/${photo.url.replace(/\\\\/g, '/')}`;
          }

          // Abrir la URL si se encontró una válida
          if (downloadUrl) {
            window.open(downloadUrl, '_blank');
          }
        }
      });

      // Cerrar el diálogo después de iniciar las descargas
      setDownloadDialogOpen(false);
    } catch (err) {
      console.error('Error al descargar las fotos:', err);
      setError('Error al descargar las fotos. Por favor, inténtalo de nuevo.');
    }
  };

  // Estilos personalizados
  const DialogActions = styled(MuiDialogActions)(({ theme }) => ({
    margin: 0,
    padding: theme.spacing(1),
  }));

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container maxWidth="md" sx={{ mt: 4, textAlign: 'center' }}>
        <Alert 
          severity="error" 
          sx={{ 
            mb: 3, 
            textAlign: 'left',
            '& .MuiAlert-message': { width: '100%' }
          }}
        >
          <Typography variant="subtitle1" fontWeight="medium" gutterBottom>
            No se pudo cargar la galería
          </Typography>
          <Typography variant="body2" component="div">
            {error}
            {error.includes('No se pudo cargar la galería') && (
              <Box mt={2}>
                <Typography variant="body2">
                  Por favor verifica que la URL sea correcta o contacta al administrador.
                </Typography>
              </Box>
            )}
          </Typography>
        </Alert>
        <Button 
          variant="contained" 
          onClick={() => window.location.href = '/'}
          sx={{ mt: 2 }}
        >
          Volver al inicio
        </Button>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      {/* Encabezado */}
      <Box sx={{ mb: 4, textAlign: 'center' }}>
        <Typography variant="h4" component="h1" gutterBottom>
          {gallery?.title || 'Galería de Fotos'}
        </Typography>
        {gallery?.selection_limit && (
          <Typography variant="subtitle1" color="text.secondary">
            Selecciona hasta {gallery.selection_limit} fotos
          </Typography>
        )}
      </Box>

      {/* Contador de selección */}
      <Box sx={{ mb: 3 }}>
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          mb: !gallery?.allow_download ? 2 : 0
        }}>
          <Typography variant="subtitle1">
            {selected.length} de {gallery?.selection_limit || 'ilimitadas'} seleccionadas
          </Typography>
        </Box>

        {!gallery?.allow_download && (
          <Alert 
            severity="info"
            sx={{
              mb: 2,
              border: '1px solid',
              borderColor: 'primary.light',
              backgroundColor: 'rgba(25, 118, 210, 0.08)',
              '& .MuiAlert-icon': {
                color: 'primary.main',
                alignItems: 'center'
              },
              '& .MuiAlert-message': {
                color: 'text.primary',
                display: 'flex',
                alignItems: 'center',
                gap: 1
              }
            }}
          >
            <Box>
              <Typography variant="subtitle2" fontWeight="medium" gutterBottom>
                Descarga deshabilitada
              </Typography>
              <Typography variant="body2">
                La descarga de fotos está deshabilitada en esta galería. Puedes seguir viendo y seleccionando las imágenes.
              </Typography>
            </Box>
          </Alert>
        )}
        <Button 
          variant="contained" 
          color="primary" 
          onClick={handleSaveSelection}
          disabled={selected.length === 0}
          startIcon={<Check />}
        >
          {selected.length > 0 ? `Guardar selección (${selected.length})` : 'Guardar selección'}
        </Button>
      </Box>

      {/* Grid de fotos */}
      <Grid container spacing={2}>
        {photos.map((photo) => {
          // Determinar qué URL de imagen usar para la vista previa
          const previewUrl = (() => {
            // Si tiene permiso para ver sin marca de agua y la imagen tiene URL sin marca
            if (gallery?.allow_final_download && photo.url) {
              return photo.url.startsWith('http') 
                ? photo.url 
                : `${API_URL}/${photo.url.replace(/\\\\/g, '/')}`;
            }

            // Si no tiene permiso, usar la versión con marca de agua si existe
            if (photo.watermarked_url) {
              return photo.watermarked_url.startsWith('http')
                ? photo.watermarked_url
                : `${API_URL}/${photo.watermarked_url.replace(/\\\\/g, '/')}`;
            }

            // Si no hay versión con marca de agua, usar la original
            if (photo.url) {
              return photo.url.startsWith('http')
                ? photo.url
                : `${API_URL}/${photo.url.replace(/\\\\/g, '/')}`;
            }

            // Si no hay ninguna URL disponible
            return '';
          })();

          return (
            <Grid item xs={12} sm={6} md={4} lg={3} key={photo.id}>
              <Card 
                sx={{ 
                  position: 'relative',
                  cursor: 'pointer',
                  border: selected.includes(photo.id) ? '3px solid #1976d2' : '3px solid transparent',
                  transition: 'border 0.2s',
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column'
                }}
                onClick={() => toggleSelect(photo.id)}
              >
                {/* Imagen */}
                <Box sx={{ 
                  position: 'relative',
                  paddingTop: '100%', // Cuadrado 1:1
                  overflow: 'hidden',
                  backgroundColor: 'rgba(0, 0, 0, 0.04)'
                }}>
                  {previewUrl ? (
                    <CardMedia
                      component="img"
                      image={previewUrl}
                      alt={photo.filename}
                      sx={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover',
                        transition: 'transform 0.3s',
                        '&:hover': {
                          transform: 'scale(1.05)'
                        }
                      }}
                    />
                  ) : (
                    <Box sx={{ 
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      width: '100%',
                      height: '100%',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: 'text.secondary'
                    }}>
                      <Typography>Imagen no disponible</Typography>
                    </Box>
                  )}

                  {/* Overlay de selección */}
                  {selected.includes(photo.id) && (
                    <Box
                      sx={{
                        position: 'absolute',
                        top: 8,
                        right: 8,
                        backgroundColor: 'primary.main',
                        color: 'white',
                        borderRadius: '50%',
                        width: 32,
                        height: 32,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        zIndex: 2
                      }}
                    >
                      <CheckCircle />
                    </Box>
                  )}
                </Box>

                {/* Acciones */}
                <CardActions sx={{ 
                  mt: 'auto', 
                  justifyContent: 'space-between',
                  p: 1,
                  gap: 1
                }}>
                  <Box sx={{ display: 'flex', gap: 1, flex: 1, minWidth: 0 }}>
                    <IconButton
                      size="small"
                      onClick={(e) => {
                        e.stopPropagation();
                        handlePreview(photo);
                      }}
                      title="Vista previa"
                      sx={{
                        color: 'text.secondary',
                        '&:hover': {
                          backgroundColor: 'rgba(0, 0, 0, 0.04)'
                        }
                      }}
                    >
                      <ZoomIn fontSize="small" />
                    </IconButton>
                    <Typography 
                      variant="body2" 
                      color="text.secondary" 
                      noWrap 
                      sx={{ 
                        alignSelf: 'center',
                        flex: 1,
                        overflow: 'hidden',
                        textOverflow: 'ellipsis'
                      }}
                    >
                      {photo.filename}
                    </Typography>
                  </Box>
                  <Button 
                    size="small" 
                    startIcon={<Download />}
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDownload(photo);
                    }}
                    disabled={!gallery?.allow_download}
                    title={!gallery?.allow_download ? 'La descarga no está permitida' : 'Descargar'}
                    sx={{ ml: 'auto' }}
                  >
                    Descargar
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          );
        })}
      </Grid>

      {/* Diálogo de descarga */}
      <Dialog 
        open={downloadDialogOpen} 
        onClose={() => setDownloadDialogOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>Descargar fotos seleccionadas</DialogTitle>
        <DialogContent>
          <DialogContentText>
            {gallery?.allow_download ? (
              `Has seleccionado ${selected.length} fotos. ¿Deseas descargarlas ahora?`
            ) : (
              <Alert severity="warning" sx={{ mb: 2 }}>
                La descarga de fotos no está permitida en esta galería.
              </Alert>
            )}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDownloadDialogOpen(false)}>Cerrar</Button>
          <Button 
            onClick={handleDownloadSelected} 
            variant="contained" 
            color="primary"
            startIcon={<Download />}
            disabled={!gallery?.allow_download || selected.length === 0}
          >
            Descargar {selected.length} {selected.length === 1 ? 'foto' : 'fotos'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Modal de vista previa */}
      <Dialog
        open={previewOpen}
        onClose={() => setPreviewOpen(false)}
        maxWidth="md"
        fullWidth
        PaperProps={{
          sx: {
            maxHeight: '90vh',
            bgcolor: 'transparent',
            boxShadow: 'none',
            overflow: 'hidden'
          }
        }}
      >
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'center',
          alignItems: 'center',
          position: 'relative',
          p: 1,
          bgcolor: 'rgba(0, 0, 0, 0.9)',
          borderRadius: 1
        }}>
          <IconButton
            onClick={() => setPreviewOpen(false)}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: 'white',
              backgroundColor: 'rgba(0, 0, 0, 0.5)',
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.7)'
              }
            }}
          >
            <Close />
          </IconButton>

          {currentPreview && (
            <Box sx={{ 
              maxHeight: '80vh',
              maxWidth: '100%',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              overflow: 'hidden'
            }}>
              <img
                src={
                  (() => {
                    // Si tiene permiso para ver sin marca de agua y la imagen tiene URL sin marca
                    if (gallery?.allow_final_download && currentPreview.url) {
                      return currentPreview.url.startsWith('http') 
                        ? currentPreview.url 
                        : `${API_URL}/${currentPreview.url.replace(/\\\\/g, '/')}`;
                    }

                    // Si no tiene permiso, usar la versión con marca de agua si existe
                    if (currentPreview.watermarked_url) {
                      return currentPreview.watermarked_url.startsWith('http')
                        ? currentPreview.watermarked_url
                        : `${API_URL}/${currentPreview.watermarked_url.replace(/\\\\/g, '/')}`;
                    }

                    // Si no hay versión con marca de agua, usar la original
                    if (currentPreview.url) {
                      return currentPreview.url.startsWith('http')
                        ? currentPreview.url
                        : `${API_URL}/${currentPreview.url.replace(/\\\\/g, '/')}`;
                    }

                    // Si no hay ninguna URL disponible
                    return '';
                  })()
                }
                alt={currentPreview.filename}
                style={{
                  maxWidth: '100%',
                  maxHeight: '80vh',
                  objectFit: 'contain',
                  borderRadius: 4,
                  // Aplicar opacidad reducida si se está mostrando con marca de agua sin permiso
                  opacity: (!gallery?.allow_final_download && currentPreview.watermarked_url) ? 0.9 : 1
                }}
                onError={(e) => {
                  // En caso de error al cargar la imagen, intentar cargar la versión con marca de agua
                  if (currentPreview.watermarked_url && !e.target.src.includes('watermarked')) {
                    e.target.src = currentPreview.watermarked_url.startsWith('http') ?
                      currentPreview.watermarked_url :
                      `${API_URL}/${currentPreview.watermarked_url.replace(/\\\\/g, '/')}`;
                  }
                }}
              />
            </Box>
          )}

          {/* Botón de descarga en la vista previa */}
          {gallery?.allow_download && currentPreview && (
            <IconButton
              onClick={(e) => {
                e.stopPropagation();
                handleDownload(currentPreview);
              }}
              sx={{
                position: 'absolute',
                right: 8,
                bottom: 8,
                color: 'white',
                backgroundColor: 'rgba(0, 0, 0, 0.5)',
                '&:hover': {
                  backgroundColor: 'rgba(0, 0, 0, 0.7)'
                }
              }}
              title="Descargar"
            >
              <Download />
            </IconButton>
          )}
        </Box>
      </Dialog>

      {/* Snackbars para mensajes */}
      <Snackbar
        open={!!message}
        autoHideDuration={6000}
        onClose={() => setMessage('')}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={() => setMessage('')} severity="info" sx={{ width: '100%' }}>
          {message}
        </Alert>
      </Snackbar>

      <Snackbar
        open={!!error}
        autoHideDuration={6000}
        onClose={() => setError('')}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={() => setError('')} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default ClientGallery;
