import React, { useEffect, useState } from 'react';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import { useParams } from 'react-router-dom';
import { 
  fetchGalleryById, 
  uploadPhotos, 
  setAllowDownload, 
  setAllowFinalDownload 
} from '../services/api';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Divider from '@mui/material/Divider';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';
import Button from '@mui/material/Button';

const GalleryManager = () => {
  const { id } = useParams();
  const [gallery, setGallery] = useState({
    id: '',
    title: '',
    is_active: false,
    allowDownload: false,
    allowFinalDownload: false,
    photos: []
  });
  const [photos, setPhotos] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previewUrls, setPreviewUrls] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState('');
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [photoToDelete, setPhotoToDelete] = useState(null);
  const [viewDialogOpen, setViewDialogOpen] = useState(false);
  const [photoToView, setPhotoToView] = useState(null);
  const [deleteAllDialogOpen, setDeleteAllDialogOpen] = useState(false);
  const owner = 'admin'; // Puedes cambiar esto por el usuario real si tienes auth

  useEffect(() => {
    let isMounted = true;

    async function loadGallery() {
      if (!id) {
        setError('ID de galería no proporcionado');
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setError(null);

        console.log(`Cargando galería con ID: ${id}`);
        const data = await fetchGalleryById(id);

        if (!isMounted) return;

        console.log('Datos recibidos de la galería:', data);

        // Asegurarse de que los valores booleanos sean booleanos
        const galleryData = {
          id: data.id || '',
          title: data.title || 'Sin título',
          is_active: Boolean(data.is_active),
          allowDownload: Boolean(data.allow_download),
          allowFinalDownload: Boolean(data.allow_final_download),
          photos: Array.isArray(data.photos) ? data.photos : []
        };

        console.log('Datos procesados de la galería:', galleryData);

        setGallery(galleryData);
        setPhotos(galleryData.photos);
      } catch (e) {
        console.error('Error al cargar la galería:', e);
        if (isMounted) {
          setError('No se pudo cargar la galería: ' + (e.message || 'Error desconocido'));
        }
      } finally {
        if (isMounted) {
          setLoading(false);
        }
      }
    }

    loadGallery();

    // Función de limpieza
    return () => {
      isMounted = false;
    };
  }, [id]);

  const handleStatusChange = async (e) => {
    if (!gallery?.id) return;

    const newStatus = e.target.checked;
    const previousStatus = gallery.is_active;

    // Actualizar estado local de inmediato para una respuesta rápida
    setGallery(g => ({ ...g, is_active: newStatus }));

    try {
      // Llamar a la API para actualizar en el servidor
      const response = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/galleries/${gallery.id}/status`, {
        method: 'PATCH',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ isActive: newStatus })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || 'Error al actualizar el estado');
      }

      const data = await response.json();

      // Actualizar el estado con la respuesta del servidor
      if (data.gallery) {
        setGallery(prev => ({
          ...prev,
          ...data.gallery,
          is_active: Boolean(data.gallery.is_active)
        }));
      }

      setMessage(`Galería ${data.gallery?.is_active ? 'activada' : 'desactivada'} correctamente`);
    } catch (err) {
      console.error('Error al actualizar estado:', err);
      // Revertir el cambio si hay error
      setGallery(g => ({ ...g, is_active: previousStatus }));
      setError(err.message || 'Error al actualizar el estado de la galería');
    }
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(files);
    setPreviewUrls(files.map(file => URL.createObjectURL(file)));
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFiles.length) return;
    setLoading(true);
    setError(null);
    try {
      await uploadPhotos({ galleryId: id, owner, files: selectedFiles });
      // Recargar galería
      const data = await fetchGalleryById(id);
      setGallery(data);
      setPhotos(data.photos || []);
      setSelectedFiles([]);
      setPreviewUrls([]);
    } catch (e) {
      setError('Error al subir fotos');
    } finally {
      setLoading(false);
    }
  };

  const handleAllowDownloadChange = async (e) => {
    const isChecked = e.target.checked;
    console.log('Switch cambiado:', isChecked, 'gallery.id:', gallery?.id);
    try {
      console.log('Llamando a setAllowDownload con:', { id: gallery.id, allowDownload: isChecked });
      const result = await setAllowDownload(gallery.id, isChecked);
      console.log('Respuesta de setAllowDownload:', result);
      setGallery(g => ({ ...g, allowDownload: isChecked }));
      setMessage('Permiso de descarga actualizado');
    } catch (error) {
      console.error('Error en handleAllowDownloadChange:', error);
      setError(`Error al actualizar permiso de descarga: ${error.message}`);
      // Revertir el cambio en la UI si falla
      setGallery(g => ({ ...g, allowDownload: !isChecked }));
    }
  };

  const handleAllowFinalDownloadChange = async (e) => {
    const isChecked = e.target.checked;
    console.log('Switch FINAL cambiado:', isChecked, 'gallery.id:', gallery?.id);
    try {
      console.log('Llamando a setAllowFinalDownload con:', { id: gallery.id, allowFinalDownload: isChecked });
      const result = await setAllowFinalDownload(gallery.id, isChecked);
      console.log('Respuesta de setAllowFinalDownload:', result);
      setGallery(g => ({ ...g, allowFinalDownload: isChecked }));
      setMessage('Permiso de descarga final actualizado');
    } catch (error) {
      console.error('Error en handleAllowFinalDownloadChange:', error);
      setError(`Error al actualizar permiso de descarga final: ${error.message}`);
      // Revertir el cambio en la UI si falla
      setGallery(g => ({ ...g, allowFinalDownload: !isChecked }));
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Gestión de Galería
        </Typography>
        {loading && <Typography color="text.secondary">Cargando...</Typography>}
        {error && <MuiAlert severity="error" sx={{ mb: 2 }}>{error}</MuiAlert>}
        {gallery ? (
          <>
            <Typography variant="h5" sx={{ mb: 2 }}>{gallery.title || 'Galería sin título'}</Typography>

            {/* Estado y permisos de galería */}
            <Paper variant="outlined" sx={{ p: 2, mb: 3 }}>
              <Typography variant="subtitle2" color="text.secondary" sx={{ mb: 1 }}>Estado de la galería</Typography>
              <FormControlLabel
                control={
                  <Switch
                    checked={gallery.is_active}
                    onChange={handleStatusChange}
                    color="primary"
                  />
                }
                label={gallery.is_active ? 'Galería activa' : 'Galería inactiva'}
                labelPlacement="start"
                sx={{ mb: 2, display: 'flex', justifyContent: 'space-between', width: '100%', m: 0 }}
              />

              <Divider sx={{ my: 2 }} />

              <Typography variant="subtitle2" color="text.secondary" sx={{ mb: 1 }}>Permisos de descarga</Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                <FormControlLabel
                  control={
                    <Switch
                      checked={gallery.allowDownload}
                      onChange={handleAllowDownloadChange}
                    />
                  }
                  label="Permitir descarga"
                  labelPlacement="start"
                  sx={{ display: 'flex', justifyContent: 'space-between', width: '100%', m: 0 }}
                />
                <FormControlLabel
                  control={
                    <Switch
                      checked={gallery.allowFinalDownload}
                      onChange={handleAllowFinalDownloadChange}
                    />
                  }
                  label="Permitir descarga final"
                  labelPlacement="start"
                  sx={{ display: 'flex', justifyContent: 'space-between', width: '100%', m: 0 }}
                />
              </Box>
            </Paper>

            <Paper variant="outlined" sx={{ p: 2, mb: 3 }}>
              <form onSubmit={handleUpload} style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
                <label style={{ flex: 1 }}>
                  <Typography variant="body1">Subir fotos (múltiple):</Typography>
                  <input type="file" multiple accept="image/*" onChange={handleFileChange} style={{ marginTop: 8 }} />
                </label>
                <Button type="submit" variant="contained" color="primary" disabled={loading || !selectedFiles.length}>
                  Subir
                </Button>
              </form>
              {previewUrls.length > 0 && (
                <div style={{ display: 'flex', gap: 8, marginTop: 16 }}>
                  {previewUrls.map((url, idx) => (
                    <img key={idx} src={url} alt="preview" style={{ width: 80, height: 80, objectFit: 'cover', borderRadius: 4, border: '1px solid #ccc' }} />
                  ))}
                </div>
              )}
            </Paper>

            <Typography variant="h6" sx={{ mb: 2 }}>Fotos en la galería</Typography>
            {photos.length === 0 ? (
              <Typography color="text.secondary">No hay fotos aún.</Typography>
            ) : (
              <Grid container spacing={2}>
                {photos.map((photo, index) => (
                  <Grid item xs={12} sm={6} md={4} lg={3} key={photo.id}>
                    <Card>
                      <CardMedia
                        component="img"
                        height="180"
                        image={
                          photo.watermarked_url
                            ? `${process.env.REACT_APP_API_URL}/${photo.watermarked_url.replace(/\\/g, '/')}`
                            : `${process.env.REACT_APP_API_URL}/${photo.url.replace(/\\/g, '/')}`
                        }
                        alt={photo.name || 'foto'}
                        style={{ objectFit: 'cover' }}
                      />
                      <CardContent>
                        <Typography variant="subtitle1" noWrap>{photo.name || `Foto #${String(index + 1).padStart(2, '0')}`}</Typography>
                        {photo.createdAt && (
                          <Typography variant="caption" color="textSecondary">
                            {new Date(photo.createdAt).toLocaleString()}
                          </Typography>
                        )}
                      </CardContent>
                      <CardActions>
                        <Button
                          variant="outlined"
                          size="small"
                          onClick={() => {
                            setPhotoToView(photo);
                            setViewDialogOpen(true);
                          }}
                        >
                          Ver
                        </Button>
                        <IconButton
                          aria-label="eliminar"
                          onClick={() => {
                            setPhotoToDelete(photo);
                            setDeleteDialogOpen(true);
                          }}
                          style={{ color: '#f44336' }}
                        >
                          <span className="material-icons">delete</span>
                        </IconButton>
                      </CardActions>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            )}
          </>
        ) : (
          <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '200px' }}>
            <Typography>Cargando información de la galería...</Typography>
          </Box>
        )}
      </Paper>
      {/* Dialogo de confirmación para eliminar foto */}
      <Dialog open={deleteDialogOpen} onClose={() => setDeleteDialogOpen(false)}>
        <DialogTitle>¿Eliminar foto?</DialogTitle>
        <DialogContent>
          <Typography>
            ¿Estás seguro de que deseas eliminar la foto {photoToDelete?.name ? `"${photoToDelete.name}"` : photoToDelete?.id ? `#${photoToDelete.id}` : ''}?
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)} color="primary">
            Cancelar
          </Button>
          <Button
            onClick={async () => {
              if (!photoToDelete) return;
              try {
                await import('../services/api').then(mod => mod.deletePhoto(photoToDelete.id));
                setPhotos(photos.filter(p => p.id !== photoToDelete.id));
                setMessage(`Foto eliminada${photoToDelete.name ? `: ${photoToDelete.name}` : ''}`);
              } catch {
                setError('Error al eliminar foto');
              } finally {
                setDeleteDialogOpen(false);
                setPhotoToDelete(null);
              }
            }}
            color="error"
            variant="contained"
          >
            Eliminar
          </Button>
        </DialogActions>
      </Dialog>

      {/* Diálogo para ver imagen */}
      <Dialog open={viewDialogOpen} onClose={() => setViewDialogOpen(false)}>
        <DialogTitle>¿Qué versión de la foto quieres ver?</DialogTitle>
        <DialogContent>
          <Typography>Selecciona una opción:</Typography>
        </DialogContent>
        <DialogActions>
          <Button
            onClick={() => {
              if (!photoToView) return;
              const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4000';
              const url = `${API_URL}/${photoToView.url.replace(/\\/g, '/')}`;
              window.open(url, '_blank');
              setViewDialogOpen(false);
              setPhotoToView(null);
            }}
            variant="outlined"
          >
            Ver original
          </Button>
          <Button
            onClick={() => {
              if (!photoToView) return;
              const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4000';
              const url = photoToView.watermarked_url
                ? `${API_URL}/${photoToView.watermarked_url.replace(/\\/g, '/')}`
                : `${API_URL}/${photoToView.url.replace(/\\/g, '/')}`;
              window.open(url, '_blank');
              setViewDialogOpen(false);
              setPhotoToView(null);
            }}
            variant="contained"
            color="primary"
          >
            Ver con marca de agua
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar para feedback */}
      <Snackbar
        open={!!message}
        autoHideDuration={3000}
        onClose={() => setMessage('')}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <MuiAlert onClose={() => setMessage('')} severity="success" sx={{ width: '100%' }}>
          {message}
        </MuiAlert>
      </Snackbar>
      {error && (
        <Snackbar
          open={!!error}
          autoHideDuration={4000}
          onClose={() => setError('')}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <MuiAlert onClose={() => setError('')} severity="error" sx={{ width: '100%' }}>
            {error}
          </MuiAlert>
        </Snackbar>
      )}
    </Container>
  );
};

export default GalleryManager;
